import React, {Component} from 'react';
import { Button, Form, FormGroup, Label, Input, Container,Modal } from 'reactstrap';
import '../App.css';

class MedInfo extends Component{
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }
  render(){
    return(
      <Container >
      <Form>
        <FormGroup className = "medFormText">
          <Label for="Medication name">Medication name</Label>
          <Input type="" name="medication name" id="example" />
        </FormGroup>
        {/* <FormGroup className = "medFormText">
          <Label for="Medication name">Medication name</Label>
          <Input type="" name="email" id="exampleEmail" placeholder="with a placeholder" />
        </FormGroup> */}
        <FormGroup  className = "medFormText" >
          <Label for="Medication name">Quantity</Label>
          <Input  type="" name="Quantity" id="exampleQuantity" />
        </FormGroup>

        <FormGroup className = "medFormText">
          <Label for="exampleDate">writen Date</Label>
          <Input
            type="date"
            name="date"
            id="exampleDate"
            placeholder="date placeholder"
          />

        </FormGroup>
        <FormGroup className = "medFormText">
          <Label for="exampleDate">writen Date</Label>
          <alert>CII medication have a life spand of 90 days from the day it is writen, CIII - CIV have a life spand of 6monthe from the day it is writen, every other medication have a life spand of 1 year 365 days from the day it is writen.</alert>
          <Input

            type="date"
            name="date"
            id="exampleDate"
            placeholder="date placeholder"
          />
          <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}></Modal>

        </FormGroup>
        <FormGroup className = "medFormText">
          <Label for="exampleSelect">refills remaining</Label>
          <Input type="select" name="select" id="exampleSelect">
            <option>1</option><option>2</option><option>3</option><option>4</option><option>5</option><option>6</option><option>7</option><option>8</option>
            <option>9</option><option>10</option><option>11</option><option>12</option>
          </Input>
        </FormGroup>

        <FormGroup className = "medFormText">
          <Label for="Medication name">Doctor</Label>
          <Input type="" name="email" id="exampleEmail" placeholder="with a placeholder" />
        </FormGroup>


        <FormGroup className = "medFormText">
          <Label for="exampleText">Text Area</Label>
          <Input type="textarea" name="text" id="exampleText" />
        </FormGroup>



        <Button>Submit</Button>
      </Form>
      </Container>
    )
  }
}
export default MedInfo;
