import React, { Component } from 'react';
import LoginContainer from './containers/LoginContainer'
import { Switch, Route, BrowserRouter as Router } from 'react-router-dom'
import MainPageContainer from './containers/MainPageContainer';
import RegisterContainer from './containers/RegisterContainer';
import NotesContainer from './containers/NotesContainer';
import LoginAndRegister from './Component/LoginAndRegister';
import SearchBar from './Component/SearchBar'
import './App.css';

class App extends Component {
  state = {
    filterMeds: '',
    meds: []
  }
  async componentDidMount (){
    const res = await fetch('https://datadiscovery.nlm.nih.gov/resource/crzr-uvwg.json')
    const json = await res.json()
    this.setState({meds: json})
    console.log(json.m)
  }
  addMeds = (id) => {
    const SelectedMeds = this.state.meds.filter(comic => comic.id)
  // if(this.state.cameras.inCart === true)
    console.log(SelectedComic)
    this.setState(prevState => {
      console.log(prevState)
      let CartItems = this.state.CartItem
      for(let i = 0; i < this.state.comic.length; i++){
        if(this.state.comic[i].id === id){
          CartItems.push(this.state.comic[i])

      }
      }
    return {CartItems};
    })
}
  MedSearch = (e) => {
    this.setState ({
      filterMeds: e.target.value

    })

  }
  render() {
    console.log(LoginContainer)
    return (
      <div className="App">

      {/* <LoginAndRegister /> */}

        <Switch>
        <Route path ="//" component={LoginAndRegister} />
        <Route path ="/searchbar" component={SearchBar} />
        <Route path="/login" component={LoginContainer} />
        <Route path="/register" component={RegisterContainer} />
        <Route exact path ="/main" component={MainPageContainer} />
        <Route exact path = "/notes" component= {NotesContainer} />


        </Switch>
      </div>
    );
  }
}

export default App;
