import React, {Component} from 'react'

class Login extends Component{
    state = {
        username: "",
        password: ""
    }

    _onSubmit = (e) => {
        e.preventDefault();
        this.props.login(this.state);
        this.setState({username: "", password: ""})
    }

    _onChange = ({target}) =>  {
        this.setState({[target.name]: target.value})
    }

    render(){
        return(
            <form action="login">
                <input type="username"/>
                <input type="password"/>
            </form>
        )
    }
}
export default Login
