    const SelectMeds = tisd.state.med.filter(meds => meds.id)
    console.log(SelectMeds)
    this.setstate
  }

  MedSearch = (e) => {
    this.setState ({
      filterMeds: e.target.value

    })

  }
  render() {
    return (
      <div className="App">
        <NavBar />
        <switch>
        <Login />
        <SearchBar />
